#import <UIKit/UIKit.h>
#import "PYLibImage.h"

#if __has_feature(objc_arc)
#define __PY_LIBIMG_CREATEIMAGE( _imageObject, _base64String )          \
    if ( _imageObject == nil ) {                                        \
        NSURL *_imageURL = [NSURL URLWithString:_base64String];         \
        NSData *_imageData = [NSData dataWithContentsOfURL:_imageURL];  \
        _imageObject = [UIImage imageWithData:_imageData];              \
    }                                                                   \
    return _imageObject
#else
#define __PY_LIBIMG_CREATEIMAGE( _imageObject, _base64String )			\
    if ( _imageObject == nil ) {										\
        NSURL *_imageURL = [NSURL URLWithString:_base64String];			\
        NSData *_imageData = [NSData dataWithContentsOfURL:_imageURL];	\
        _imageObject = [[UIImage imageWithData:_imageData] retain];     \
    }																	\
    return _imageObject

#endif
static NSString * _base64SwitchBackground = @"data:image/png;base64,";
static NSString * _base64SwitchButton = @"data:image/png;base64,";
static NSString * _base64NavBarTransBkg = @"data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAQAAAAECAYAAACp8Z5+AAAABHNCSVQICAgIfAhkiAAAAAlwSFlzAAALEgAACxIB0t1+/AAAABR0RVh0Q3JlYXRpb24gVGltZQA2LzEvMTJPrnVGAAAAHHRFWHRTb2Z0d2FyZQBBZG9iZSBGaXJld29ya3MgQ1M1cbXjNgAAABRJREFUCJlj/P//PwMyYGJAA4QFAMaOAwWcosBWAAAAAElFTkSuQmCC";
static NSString * _base64SlideBackground = @"data:image/png;base64,";
static NSString * _base64SlideButton = @"data:image/png;base64,";
static NSString * _base64SlideMinimum = @"data:image/png;base64,";
static NSString * _base64SlideMaximum = @"data:image/png;base64,";
static NSString * _base64CalendarPrevMonth = @"data:image/png;base64,";
static NSString * _base64CalendarPrevYear = @"data:image/png;base64,";
static NSString * _base64CalendarNextMonth = @"data:image/png;base64,";
static NSString * _base64CalendarNextYear = @"data:image/png;base64,";
static NSString * _base64CalendarWeekend = @"data:image/png;base64,";
static NSString * _base64CalendarWeekday = @"data:image/png;base64,";
static NSString * _base64FrameShadowLeft = @"data:image/png;base64,";
static NSString * _base64FrameShadowRight = @"data:image/png;base64,";

@implementation PYLibImage

+(UIImage *)imageForKey:(PYLibImageIndex)imgIndex
{
	static UIImage *_imgSwitchBkg = nil;
	static UIImage *_imgSwitchBtn = nil;
	static UIImage *_imgNavbarTransBkg = nil;
	static UIImage *_imgSlideBkg = nil;
	static UIImage *_imgSlideBtn = nil;
	static UIImage *_imgSlideMinTrack = nil;
	static UIImage *_imgSlideMaxTrack = nil;
	static UIImage *_imgCalendarPrevMonth = nil;
	static UIImage *_imgCalendarPrevYear = nil;
	static UIImage *_imgCalendarNextMonth = nil;
	static UIImage *_imgCalendarNextYear = nil;
	static UIImage *_imgCalendarWeekEnd = nil;
	static UIImage *_imgCalendarWeekDay = nil;
	static UIImage *_imgFrameShadowLeft = nil;
	static UIImage *_imgFrameShadowRight = nil;
	
	switch (imgIndex) 
	{
		case PYLibImageIndexSwitchBkg:
			__PY_LIBIMG_CREATEIMAGE(_imgSwitchBkg, _base64SwitchBackground);
			break;
		case PYLibImageIndexSwitchBtn:
			__PY_LIBIMG_CREATEIMAGE(_imgSwitchBtn, _base64SwitchButton);
			break;
		case PYLibImageIndexNavbarTBkg:
			__PY_LIBIMG_CREATEIMAGE(_imgNavbarTransBkg, _base64NavBarTransBkg);
			break;
 		case PYLibImageIndexSlideBkg:
 			__PY_LIBIMG_CREATEIMAGE(_imgSlideBkg, _base64SlideBackground);
 			break;
  		case PYLibImageIndexSlideBtn:
  			__PY_LIBIMG_CREATEIMAGE(_imgSlideBtn, _base64SlideButton);
  			break;
		case PYLibImageIndexSlideMin:
			__PY_LIBIMG_CREATEIMAGE(_imgSlideMinTrack, _base64SlideMinimum);
			break;
		case PYLibImageIndexSlideMax:
			__PY_LIBIMG_CREATEIMAGE(_imgSlideMaxTrack, _base64SlideMaximum);
			break;
		case PYLibImageCalendarPrevMonth:
			__PY_LIBIMG_CREATEIMAGE(_imgCalendarPrevMonth, _base64CalendarPrevMonth);
			break;
		case PYLibImageCalendarPrevYear:
			__PY_LIBIMG_CREATEIMAGE(_imgCalendarPrevYear, _base64CalendarPrevYear);
			break;
		case PYLibImageCalendarNextMonth:
			__PY_LIBIMG_CREATEIMAGE(_imgCalendarNextMonth, _base64CalendarNextMonth);
			break;
		case PYLibImageCalendarNextYear:
			__PY_LIBIMG_CREATEIMAGE(_imgCalendarNextYear, _base64CalendarNextYear);
			break;
		case PYLibImageCalendarWeekEnd:
			__PY_LIBIMG_CREATEIMAGE(_imgCalendarWeekEnd, _base64CalendarWeekend);
			break;
		case PYLibImageCalendarWeekDay:
			__PY_LIBIMG_CREATEIMAGE(_imgCalendarWeekDay, _base64CalendarWeekday);
			break;
		case PYLibImageFrameShadowLeft:
			__PY_LIBIMG_CREATEIMAGE(_imgFrameShadowLeft, _base64FrameShadowLeft);
			break;
		case PYLibImageFrameShadowRight:
			__PY_LIBIMG_CREATEIMAGE(_imgFrameShadowRight, _base64FrameShadowRight);
			break;
		default:
			return nil;
	}
	
}

@end
